import React, { useState, useEffect, createContext, useContext } from 'react';
import { BrowserRouter, Routes, Route, Navigate, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { 
  BarChart3, Calendar, CheckCircle, XCircle, Clock, DollarSign,
  Users, FileText, TrendingUp, LogOut, User as UserIcon, Building2,
  Plus, Eye, Edit, Trash2, Search, Filter, Download, Upload
} from 'lucide-react';
import './App.css';

// API Configuration
const API_BASE_URL = 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json'
  }
});

// Add auth token to requests
api.interceptors.request.use((config) => {
  const token = localStorage.getItem('token');
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

// Auth Context
const AuthContext = createContext(null);

const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const response = await api.get('/auth/me');
        setUser(response.data);
      } catch (error) {
        localStorage.removeItem('token');
      }
    }
    setLoading(false);
  };

  const login = async (email, password, role) => {
    const response = await api.post('/auth/login', { email, password, role });
    localStorage.setItem('token', response.data.access_token);
    setUser(response.data.user);
    return response.data;
  };

  const register = async (userData) => {
    const response = await api.post('/auth/register', userData);
    localStorage.setItem('token', response.data.access_token);
    setUser(response.data.user);
    return response.data;
  };

  const logout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, loading, login, register, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

const useAuth = () => useContext(AuthContext);

const HomePage = () => (
  <div className="auth-container">
    <div className="auth-card">
      <div className="auth-header">
        <div className="auth-logo">
          <Calendar size={32} />
        </div>
        <h1 className="auth-title">EventFlow</h1>
        <p className="auth-subtitle">Manage events and vendors in one place</p>
      </div>
      <div className="text-center">
        <Link to="/events" className="btn btn-secondary btn-lg" style={{ marginRight: '0.75rem' }}>
          View Events
        </Link>
        <Link to="/register" className="btn btn-secondary btn-lg" style={{ marginRight: '0.75rem' }}>
          Register
        </Link>
        <Link to="/login" className="btn btn-primary btn-lg">Sign In</Link>
      </div>
    </div>
  </div>
);

// Protected Route Component
const ProtectedRoute = ({ children, role }) => {
  const { user, loading } = useAuth();

  if (loading) {
    return <div className="loading"><div className="spinner"></div></div>;
  }

  if (!user) {
    return <Navigate to="/login" />;
  }

  if (role && user.role !== role) {
    return <Navigate to="/" />;
  }

  return children;
};

// Navigation Component
const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (!user) return null;

  const isVendor = user.role === 'vendor';
  const isAdmin = user.role === 'admin';

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <div className="navbar-brand-icon">
          <Calendar size={20} />
        </div>
        <span>EventFlow</span>
      </div>

      <ul className="navbar-nav">
        <li><Link to={isAdmin ? "/admin" : "/dashboard"} className="nav-link">Dashboard</Link></li>
        {isVendor && (
          <>
            <li><Link to="/events" className="nav-link">Events</Link></li>
            <li><Link to="/my-applications" className="nav-link">My Applications</Link></li>
            <li><Link to="/payments" className="nav-link">Payments</Link></li>
          </>
        )}
        {isAdmin && (
          <>
            <li><Link to="/vendors" className="nav-link">Vendors</Link></li>
            <li><Link to="/applications" className="nav-link">Applications</Link></li>
            <li><Link to="/admin/events" className="nav-link">Events</Link></li>
            <li><Link to="/payments" className="nav-link">Payments</Link></li>
          </>
        )}
      </ul>

      <div className="user-menu">
        <div className="user-avatar">
          {user.full_name?.charAt(0) || 'U'}
        </div>
        <button className="btn btn-ghost btn-sm" onClick={handleLogout}>
          <LogOut size={16} />
          Logout
        </button>
      </div>
    </nav>
  );
};

// Login Page
const LoginPage = () => {
  const [formData, setFormData] = useState({ email: '', password: '', role: 'vendor' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const data = await login(formData.email, formData.password, formData.role);
      navigate(data.user?.role === 'admin' ? '/admin' : '/dashboard');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <div className="auth-logo">
            <Calendar size={32} />
          </div>
          <h1 className="auth-title">Welcome Back</h1>
          <p className="auth-subtitle">Sign in to your account</p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="role-selector">
            <div
              className={`role-option ${formData.role === 'vendor' ? 'active' : ''}`}
              onClick={() => setFormData({ ...formData, role: 'vendor' })}
            >
              <div className="role-icon"><Building2 size={32} /></div>
              <div><strong>Vendor</strong></div>
            </div>
            <div
              className={`role-option ${formData.role === 'admin' ? 'active' : ''}`}
              onClick={() => setFormData({ ...formData, role: 'admin' })}
            >
              <div className="role-icon"><UserIcon size={32} /></div>
              <div><strong>Admin</strong></div>
            </div>
          </div>

          {error && <div className="form-error text-center mb-3">{error}</div>}

          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              type="email"
              className="form-input"
              placeholder="your@email.com"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-input"
              placeholder=""
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              required
            />
          </div>

          <button type="submit" className="btn btn-primary btn-lg" style={{ width: '100%' }} disabled={loading}>
            {loading ? 'Signing in...' : 'Sign In'}
          </button>

          <div className="text-center mt-4">
            <p className="text-muted">
              Demo Credentials:<br />
              Vendor: vendor1@example.com / vendor123<br />
              Admin: admin@eventflow.com / admin123
            </p>
            <p className="text-muted mt-3">
              New vendor? <Link to="/register">Create an account</Link>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

const RegisterPage = () => {
  const [formData, setFormData] = useState({
    full_name: '',
    email: '',
    password: '',
    phone: '',
    company_name: '',
    business_type: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { register } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    try {
      await register({
        ...formData,
        role: 'vendor'
      });
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <div className="auth-header">
          <div className="auth-logo">
            <Calendar size={32} />
          </div>
          <h1 className="auth-title">Create Vendor Account</h1>
          <p className="auth-subtitle">Register to apply for events</p>
        </div>

        <form onSubmit={handleSubmit}>
          {error && <div className="form-error text-center mb-3">{error}</div>}

          <div className="form-group">
            <label className="form-label">Full Name</label>
            <input
              type="text"
              className="form-input"
              value={formData.full_name}
              onChange={(e) => setFormData({ ...formData, full_name: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Email Address</label>
            <input
              type="email"
              className="form-input"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-input"
              value={formData.password}
              onChange={(e) => setFormData({ ...formData, password: e.target.value })}
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Phone</label>
            <input
              type="text"
              className="form-input"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
            />
          </div>

          <div className="form-group">
            <label className="form-label">Company Name</label>
            <input
              type="text"
              className="form-input"
              value={formData.company_name}
              onChange={(e) => setFormData({ ...formData, company_name: e.target.value })}
            />
          </div>

          <div className="form-group">
            <label className="form-label">Business Type</label>
            <input
              type="text"
              className="form-input"
              value={formData.business_type}
              onChange={(e) => setFormData({ ...formData, business_type: e.target.value })}
            />
          </div>

          <button type="submit" className="btn btn-primary btn-lg" style={{ width: '100%' }} disabled={loading}>
            {loading ? 'Creating account...' : 'Register'}
          </button>

          <div className="text-center mt-4">
            <p className="text-muted">
              Already have an account? <Link to="/login">Sign in</Link>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

// Vendor Dashboard
const VendorDashboard = () => {
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const { user } = useAuth();

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const response = await api.get('/vendor/dashboard/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="loading"><div className="spinner"></div></div>;

  return (
    <div>
      <h1>Welcome back, {user.full_name}!</h1>
      <p className="text-muted mb-4">{user.company_name || 'Vendor Dashboard'}</p>

      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-label">Total Applications</div>
          <div className="stat-value">{stats?.total_applications || 0}</div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Pending Review</div>
          <div className="stat-value">{stats?.pending || 0}</div>
          <div className="stat-change">
            <Clock size={16} />
            Awaiting response
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Approved</div>
          <div className="stat-value">{stats?.approved || 0}</div>
          <div className="stat-change positive">
            <CheckCircle size={16} />
            Active events
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-label">Total Paid</div>
          <div className="stat-value">${stats?.total_paid?.toFixed(2) || '0.00'}</div>
          <div className="stat-change">
            <DollarSign size={16} />
            Completed
          </div>
        </div>
      </div>

      <div className="grid grid-2 gap-4">
        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Quick Actions</h3>
          </div>
          <div className="card-body">
            <Link to="/events" className="btn btn-primary btn-lg" style={{ width: '100%', marginBottom: '1rem' }}>
              <Plus size={20} />
              Browse & Apply to Events
            </Link>
            <Link to="/my-applications" className="btn btn-secondary btn-lg" style={{ width: '100%' }}>
              <FileText size={20} />
              View My Applications
            </Link>
          </div>
        </div>

        <div className="card">
          <div className="card-header">
            <h3 className="card-title">Application Status</h3>
          </div>
          <div className="card-body">
            <div style={{ marginBottom: '1rem' }}>
              <div className="flex justify-between mb-2">
                <span>Approved</span>
                <strong className="text-success">{stats?.approved || 0}</strong>
              </div>
              <div className="flex justify-between mb-2">
                <span>Pending</span>
                <strong className="text-accent">{stats?.pending || 0}</strong>
              </div>
              <div className="flex justify-between">
                <span>Rejected</span>
                <strong className="text-danger">{stats?.rejected || 0}</strong>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// Vendor Events List
const VendorEventsList = () => {
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const { user, loading: authLoading } = useAuth();
  const navigate = useNavigate();
  const [applicationData, setApplicationData] = useState({
    product_service: '',
    booth_requirements: '',
    additional_notes: ''
  });

  useEffect(() => {
    if (!authLoading) {
      fetchEvents();
    }
  }, [authLoading, user]);

  const fetchEvents = async () => {
    try {
      const isVendor = user?.role === 'vendor';
      const response = await api.get(isVendor ? '/vendor/events' : '/events');
      setEvents(response.data || []);
    } catch (error) {
      console.error('Error fetching events:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleApply = (event) => {
    if (!user) {
      navigate('/login');
      return;
    }
    if (user.role !== 'vendor') {
      alert('Only vendor accounts can apply to events.');
      return;
    }
    setSelectedEvent(event);
    setShowModal(true);
  };

  const handleSubmitApplication = async (e) => {
    e.preventDefault();
    try {
      await api.post('/vendor/applications', {
        event_id: selectedEvent.id,
        ...applicationData
      });
      alert('Application submitted successfully!');
      setShowModal(false);
      setApplicationData({ product_service: '', booth_requirements: '', additional_notes: '' });
      fetchEvents();
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to submit application');
    }
  };

  if (authLoading || loading) return <div className="loading"><div className="spinner"></div></div>;

  return (
    <div>
      <h1>Available Events</h1>
